import React,{useState} from 'react';


const CoustomerData = () => {  

  const [bal1,setBal1]=useState(2000);
  const [bal2,setBal2]=useState(3000);
  const [bal3,setBal3]=useState(4000);
  const [inputValue,setInputValue] = useState()
  const inputHandeler = (event)=>{
    console.log(event.target.value);
    console.log(bal1);
    setInputValue(Number(event.target.value))
  }
  const btnHandeler = () =>{
    setBal1(bal1-inputValue)
    setBal2(bal2+inputValue)
  }
    return (

  <div>
    <h2>Balance</h2>
    {/* input */}
    <h3>trandfer from person1 to person2</h3>
    <input onChange={inputHandeler} type='Number'/>
    <button onClick={btnHandeler} >transfer</button>
    <table>
      <thead>
        <tr>
          <th>Firstname</th>
          <th>Lastname</th>
          <th>Balance</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>John</td>
          <td>Doe</td>
          <td>{bal1}</td>
        </tr>
        <tr>
          <td>Mary</td>
          <td>Moe</td>
          <td>{bal2}</td>
        </tr>
        <tr>
          <td>July</td>
          <td>Dooley</td>
          <td>{bal3}</td>
        </tr>
      </tbody>
    </table>
</div>
    )
}

export default CoustomerData;
