import { useState } from 'react';
import Banking1 from './components/Banking/Banking1'
function App() {

  // g
  const [bal, setbal] = useState({
    value1: 2000,
    value2: 600,
    value3: 400,
  });

  //input which we are giving for amt
  const [inputValue, setInputValue] = useState();

  const inputHandeler = (event) => {
    console.log(event.target.value);
    setInputValue(Number(event.target.value));
  };

  const [dropdownValue1, setdropdownVAlue1] = useState();

  const optionHandel1 = (event) => {
    setdropdownVAlue1(event.target.value);
  };

  const [dropdownValue2, setdropdownVAlue2] = useState();

  const optionHandel2 = (event) => {
    setdropdownVAlue2(event.target.value);
  };

  const transferHandeler = () => {
    // let updateValue1 = dropdownValue1
    console.log(bal.value1);
    console.log(bal.value1 + inputValue);
    console.log(dropdownValue1);
    setbal({
      ...bal,
      value1: bal.dropdownValue1 + inputValue,
      dropdownValue2: bal.dropdownValue2 - inputValue,
    });
  };
  //h
  return (
    <div style={{textAlign:'center'}}>
      <Banking1 inputHandeler={inputHandeler} bal={bal} />
    </div>
  );
}

export default App;
