import React,{useState} from 'react';


const CoustomerData = () => {  
//initial balance state 
  const [bal1,setbal1]=useState(2000);
  const [bal2,setbal2]=useState(3000);
  const [bal3,setbal3]=useState(4000);

  //input which we are taking -- for direct base
  const [inputValue,setInputValue] = useState()

  const inputHandeler = (event)=>{
    console.log(event.target.value);
    console.log(bal1);
    setInputValue(Number(event.target.value))
  }

  const btnHandeler = () =>{
    setbal1(bal1-inputValue)
    setbal2(bal2+inputValue)
  }
  const [dropdownValue,setdropdownVAlue]=useState('initiak');

  const optionHandel=(event)=>{
    setdropdownVAlue(event.target.value)
  }

    return (
  <div>
    <select onChange={optionHandel}>
      <option value="bal1">jhon</option>
      <option value="bal2">marry</option>
      <option value="bal3">july</option>
    </select>
    <h1>{dropdownValue}</h1>
    <h2>Balance</h2>
    {/* input */}
    <h3>Transfer from person1 to person2</h3>
    <input onChange={inputHandeler} type='Number'/>
    <button onClick={btnHandeler} >transfer</button>
    <table>
      <thead>
        <tr>
          <th>Firstname</th>
          <th>Lastname</th>
          <th>Balance</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>John</td>
          <td>Doe</td>
          <td>{bal1}</td>
        </tr>
        <tr>
          <td>Mary</td>
          <td>Moe</td>
          <td>{bal2}</td>
        </tr>
        <tr>
          <td>July</td>
          <td>Dooley</td>
          <td>{bal3}</td>
        </tr>
      </tbody>
    </table>
</div>
    )
}
export default CoustomerData;
